import React, { useRef } from "react";
import styled from "styled-components";

const ListComponent = styled.div`
    text-align: center;
    padding: 10px;
    width: 200px;
    height: calc(100vh - 60px);
    overflow-y: scroll;
    overflow-x: hidden;
    margin: 0 5px 0 5px;
    clip-path: inset(0 -30px 0 0);
`;

// box-shadow:  ${({ ref }) => ref?.current.offsetHeight > 0 ? "inset 0px 11px 8px -5px #CCC, inset 0px -11px 8px -5px #CCC;" : ""};


const List = ({ innerRef, onDragOver = undefined , onDrop = undefined, children}) => {
    return (
        <ListComponent
            ref={innerRef}
            onDragOver={onDragOver}
            onDrop={onDrop}>
            {children}
        </ListComponent>);
};

export default List;
