import React, { useState } from "react";
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import styled from "styled-components";
import { AircraftCard } from "./components/AircraftCard";
import Flight from "./components/Flight/Flight";
import { List } from "./components/List";
import { move } from "./utils/helpers";
import Timeline from "./components/Timeline/Timeline";
import { aircrafts, flights } from "./utils/data";

const Wrapper = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const Header = styled.h2`
    padding-bottom: 5px;
`;


function App() {
    const [ rotation, setRotation ] = useState(flights.slice(2,1));
    const [ availableFlights, setAvailableFlights ] = useState(flights);

    const onDragEnd = (result) => {
        const { source, destination, draggableId } = result;

        // Dropped outside the list
        if (!destination) {
            return;
        }

        if (source.droppableId == "rotation") {
            return;
        }

        if (destination.droppableId === "droppable") return;

        if (destination.droppableId == "rotation") {

            // Departure Airport of the flight must be the same of the previous flight
            if(checkIfValid(draggableId, destination.index)) {
                const r = move(availableFlights, rotation, source, destination);
                setRotation(r.rotation);
                setAvailableFlights(r.droppable);
            }

            return;
        }

    };

    const checkIfValid = (flightId, previousFlightId) => {
        console.log("checkIfValid", previousFlightId);
        const flight = flights.find(f => f.ident === flightId);
        const newRotationArray = [ ...rotation, flight];
        const previousFlight = newRotationArray[previousFlightId - 1] || null;
        const nextFlight = newRotationArray[previousFlightId + 1] || null;

        // First flight in rotation
        if(!previousFlight && !nextFlight) {
            console.log(previousFlight, nextFlight);
            return true;
        }



        if(previousFlight){
            console.log('previous flight')
            if( previousFlight.departuretime + 1200 <= flight.arrivaltime && previousFlight.destination === flight.origin) {
                console.log('previous flight => yes')
                return true;
            }
        }
        console.log("2");

        if(nextFlight){
            console.log('next flight')
            if(flight.departuretime + 1200 <= nextFlight.arrivaltime && flight.destination === nextFlight.origin) {
                console.log('next flight => yes')

                return true;
            }
        }

        return false;
    }

    const calculateTimePeriods = (rotation) => (
         rotation.reduce((acc, value, index) => {
            if(index === 0) {
                acc.push({ type: "Free", duration: value.departuretime});
            }

            acc.push({ type: "Busy", duration: value.arrivaltime - value.departuretime});

            if(rotation[index + 1]){
                acc.push({ type: "Turnaround", duration: 1200});
                acc.push({ type: "Free", duration:  rotation[index + 1].departuretime - value.arrivaltime + 1200});
            } else {
                acc.push({ type: "Free", duration:  86400 - value.arrivaltime});
            }

            return acc;
        },  [])
    );

    const calculateUsage = (timeline) => {
        const busyPeriods = timeline.filter((period) => period.type === "Busy");

        const sum = busyPeriods.reduce(
            (acc, currentValue) => acc + currentValue.duration,
            0
          );

        return (sum / 86400) * 100
    }


    return (
        <DragDropContext onDragEnd={onDragEnd}>
            <div className="App">
                <Wrapper>
                    {/* Aircrafts List */}
                    <div style={{ display: "flex", flexDirection: "column", alignItems: "center"}}>
                        <Header>Aircrafts</Header>
                        <List>
                            {aircrafts.map((aircraft) => (
                                <AircraftCard key={aircraft.ident} aircraft={aircraft} percentage={calculateUsage(calculateTimePeriods(rotation))} />
                            ))}
                        </List>
                    </div>
                    {/* Rotation */}
                    <div style={{ display: "flex", flexDirection: "column", alignItems: "center",  width: "250px" }}>
                        <Header>Rotation</Header>

                        <Droppable droppableId={"rotation"}>
                            {(provided) => (
                                <div ref={provided.innerRef} style={{  width: "200px"}}>
                                    {rotation.map((flight, index) => (
                                        <Flight
                                        isDragDisabled={true}
                                        key={flight.ident}
                                        index={index}
                                        flight={flight}
                                    />))}
                                    {provided.placeholder}
                                </div>
                            )}
                        </Droppable>
                        <Timeline periods={calculateTimePeriods(rotation)} />
                     </div>
                    {/* Flight List */}
                    <div style={{ display: "flex", flexDirection: "column", alignItems: "center",  width: "250px" }}>
                    <Header>Available Flights</Header>
                    <Droppable droppableId={"droppable"}>
                        {(provided) => (
                            <div  ref={provided.innerRef}>
                                {availableFlights.map((flight, index) => (
                                    <Flight
                                        key={flight.ident}
                                        index={index}
                                        flight={flight}
                                    />)
                                )}
                                {provided.placeholder}
                            </div>)}
                    </Droppable>
                    </div>
                </Wrapper>
            </div>
        </DragDropContext>);
}

export default App;
