import styled from "styled-components";

const Content = styled.div`
    display: flex;
    flex: 0 1 24%;
    font-size: 10px;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    height: 100px;
    width: 200px;

    border-radius: 5px;

    box-shadow: 1px 3px 8px 3px rgb(0 0 0 / 20%);

    margin-top: 20px; 
    padding: 6px;

    background-color: ${({isDragging}) => isDragging ? "antiquewhite" : "white"};

`;

const InfoWrapper = styled.div`
    display: flex;
    justify-content: space-between;
    width: 100%;
`;

const Button = styled.button`
    position: relative;
    top: 0;
    margin-top: -14px;
    left: 50%;
    border-radius: 50%;
    height: 25px;
    width: 25px;
    border: 0;
    box-shadow: 0px 10px 13px #cc
`;

export {
    Content,
    InfoWrapper,
    Button
};
