import React from "react";
import { Draggable } from "react-beautiful-dnd";

const Content = styled.div`
    display: flex;
    flex: 0 1 24%;
    font-size: 10px;
    flexDirection: column;
    justify-content: center;
    align-items: center;

    height: 100px;
    width: 100%;

    border: 2px solid black;
    border-radius: 5px;

    box-shadow: 5px 5px 19px #ccc;

    margin-top: 6px; 
    padding: 6px;
`;

const InfoWrapper = styled.div`
    display: flex;
    justify-content: space-between;
    width: 100%;
`;

const Flight = ({ flight, index }) => {
    return (
        <Draggable key={flight.ident} draggableId={flight.ident} index={index}>
            {(provided) => (
                <Content ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                    <div>
                        <h3>{ident}</h3>
                    </div>
                    <InfoWrapper>
                        <div>
                            <p>{readable_departure}</p>
                            <p>{origin}</p>
                        </div>
                        <div>
                            <p>{readable_arrival}</p>
                            <p>{destination}</p>
                        </div>
                    </InfoWrapper>
                </Content>
            )}
        </Draggable>
    );
};

export default Flight;
