import React from "react";
import { Card } from "../Card";
import styled from "styled-components";

const Row = styled.div`
   display: flex;
   flex-direction: row;
`;

const Column = styled.div`
   display: flex;
   flex-direction: column;
`;

type Flight = {
    ident: string,
    departuretime: number,
    arrivaltime: number,
    readableDeparture: string,
    readableArrival: string,
    origin: string,
    destination: string,
} 


type Props = {
    flight: Flight,
    onDragStart,
} 


const FlightCard = ({ flight, onDragStart} : Props) => {
    const { ident,
            departuretime,
            arrivaltime,
            readableDeparture,
            readableArrival,
            origin,
            destination } = flight;

    return (<Card onDragStart={onDragStart} draggable={true}>
        <p>{ident}</p>
        <Row>
            <Column>
                <p>{readableDeparture}</p>
                <p>{origin}</p>
            </Column>
            <Column>
                <p>{readableArrival}</p>
                <p>{destination}</p>
            </Column>
        </Row>
    </Card>);
};

export default FlightCard;
