import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 250px 
`;

const Header = styled.h2`
    padding-bottom: 5px;
`;

const ListComponent = styled.div`
    text-align: center;
    padding: 10px;
    width: 200px;
    height: calc(100vh - 150px);
    margin: 0 5px 0 5px;
`;

// box-shadow:  ${({ ref }) => ref?.current.offsetHeight > 0 ? "inset 0px 11px 8px -5px #CCC, inset 0px -11px 8px -5px #CCC;" : ""};


const List = ({ id, title, innerRef, children}) => {
    return (
        <Wrapper>
            <Header>Rotation</Header>
            <Droppable droppableId={"rotation"}>
                <ListComponent ref={innerRef} >
                    {children}
                </ListComponent>
            </Droppable>
            <Timeline periods={periods} />
        </Wrapper>
    );
};

export default List;
