/* eslint-disable react/jsx-indent */
/* eslint-disable indent */
import React, { useState } from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import styled from "styled-components";
import { AircraftCard } from "./components/AircraftCard";
import Flight from "./components/Flight/Flight";
import { List } from "./components/List";
import { RotationCard } from "./components/RotationCard";
import { aircrafts, flights } from "./utils/data";

const Wrapper = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const Header = styled.h2`
    padding-bottom: 5px;
`;

function App() {
    const [columns, setColumns] = useState([]);

    const [ rotation, setRotation ] = useState(flights.slice(2,1));
    const [ availableFlights, setAvailableFlights ] = useState(flights);

    // a little function to help us with reordering the result
    const reorder = (list, startIndex, endIndex) => {
        const result = Array.from(list);
        const [removed] = result.splice(startIndex, 1);
        result.splice(endIndex, 0, removed);

        return result;
    };

    // /**
    //  * Moves an item from one list to another list.
    //  */
    // const move = (source, destination, droppableSource, droppableDestination) => {
    //     const sourceClone = Array.from(source);
    //     const destClone = Array.from(destination);
    //     const [removed] = sourceClone.splice(droppableSource.index, 1);

    //     destClone.splice(droppableDestination.index, 0, removed);

    //     const result = {};
    //     result[droppableSource.droppableId] = sourceClone;
    //     result[droppableDestination.droppableId] = destClone;

    //     return result;
    // };



    // const onDragEnd = (result) => {
    //     const { source, destination, draggableId } = result;

    //     // Dropped outside the list
    //     if (!destination) {
    //         return;
    //     }

    //     // Dropped in the same list
    //     if (result.destination.index === result.source.index) return;


    //     if (source.droppableId === "droppable") {
    //         const flight = flights.find(f => f.ident === draggableId);

    //         setRotation(prev => [...prev, flight]);
    //         setAvailableFlights(prev => prev.filter((p) =>  p.ident !== draggableId));
    //     }

    //     const projects = reorder(
    //         flights, // project is state 
    //         result.source.index,
    //         result.destination.index
    //       );
    //       //store reordered state.

    //     //   setProject(projects);

    // };


  const onDragEnd = (result, columns, setColumns) => {
    if (!result.destination) return;

    const { source, destination } = result;

    if (source.droppableId !== destination.droppableId) {
      const sourceColumn = columns[source.droppableId];
      const destColumn = columns[destination.droppableId];
      const sourceItems = [...sourceColumn.items];
      const destItems = [...destColumn.items];
      const [removed] = sourceItems.splice(source.index, 1);

      destItems.splice(destination.index, 0, removed);

      setColumns({
        ...columns,
        [source.droppableId]: {
          ...sourceColumn,
          items: sourceItems,
        },
        [destination.droppableId]: {
          ...destColumn,
          items: destItems,
        },
      });
    } else {
      const column = columns[source.droppableId];
      const copiedItems = [...column.items];
      const [removed] = copiedItems.splice(source.index, 1);
      copiedItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...column,
          items: copiedItems,
        },
      });
    }
  };

    return (
        <DragDropContext onDragEnd={onDragEnd}>
            <div className="App">
                <div>Header</div>
                <Wrapper>

                    {/* Aircrafts List */}
                    <Header>Aircrafts</Header>
                    <List>
                        {aircrafts.map((aircraft) => (
                            <AircraftCard key={aircraft.ident} aircraft={aircraft} />
                        ))}
                    </List>

                    <Droppable droppableId={"rotation"}>
                        {(provided, snapshot) => (
                            <>
                        {/* Rotation */}
                                <Header>Rotation</Header>
                                <List innerRef={provided.innerRef}>
                                    {rotation.map((flight) => {
                                        return (
                                        <RotationCard
                                            key={flight.ident}
                                            flight={flight}
                                        />
                                    );
                                    })}
                                </List>

                                {/* Flight List */}
                                <div>
                                    {availableFlights.map((flight, index) => (
                                        <Flight 
                                            key={flight.ident}
                                            index={index}
                                            flight={flight}
                                        />)
                                    )}
                                    {provided.placeholder}
                                </div>
                            </>)}
                    </Droppable>
                </Wrapper>

            </div>
        </DragDropContext>
    );
}

export default App;
