import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
    position: relative;
    display: inline-block;
    border-bottom: 1px dotted black;

    &:hover ${Hover} {
        visibility: visible;
      }
`

const Hover = styled.div`
`


const Tooltip = ({ children }) => {
    return (
        <Wrapper>
            <Hover> {children}</Hover>
        </Wrapper>)
}
