import React from "react";
import styled from "styled-components";

const Wrapper = styled.div`
    font-size: 10px;
    display: flex;
    flex: 0 1 24%;
    align-items: center;
    height: 100px;
    width: 100%;
    border: 2px solid black;
    justify-content: center;
    margin-top: 6px;
    box-shadow: 
    border-radius: 5px;
    box-shadow: 5px 5px 19px #ccc;
`;

const Card = ({ innerRef, dragProps, onDragStart = undefined, draggable = false, children }) => {

    return (
        <Wrapper
            ref={innerRef}
            {...dragProps.draggableProps}
            {...dragProps.dragHandleProps}
            onDragStart={onDragStart}
            draggable={draggable}>
            {children}
        </Wrapper>
    );
};

export default Card;
