import React from "react";
import { Card } from "../Card";
import styled from "styled-components";

const InfoWrapper = styled.div`
    display: flex;
    justify-content: space-between;
    width: 100%;
`;


type Flight = {
    ident: string,
    departuretime: number,
    arrivaltime: number,
    readable_departure: string,
    readable_arrival: string,
    origin: string,
    destination: string,
} 


type Props = {
    flight: Flight,
    onDragStart: any,
} 


const FlightCard = ({ flight, onDragStart} : Props) => {
    const { ident, readable_departure, readable_arrival, origin, destination } = flight;

    return (
        <Card onDragStart={onDragStart} draggable={true} >
            <div style={{ flexDirection: "column", width: "100%", padding: "6px" }}>
                <div>
                    <p>{ident}</p>
                </div>
                <InfoWrapper>
                    <div>
                        <p>{readable_departure}</p>
                        <p>{origin}</p>
                    </div>
                    <div>
                        <p>{readable_arrival}</p>
                        <p>{destination}</p>
                    </div>
                </InfoWrapper>
            </div>
        </Card>);
};

export default FlightCard;
