import React, { useState } from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import styled from "styled-components";
import { AircraftCard } from "./components/AircraftCard";
import Flight from "./components/Flight/Flight";
import { FlightCard } from "./components/FlightCard";
import { List } from "./components/List";
import { RotationCard } from "./components/RotationCard";
import { aircrafts, flights } from "./utils/data";


const Wrapper = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const Header = styled.h2`
    padding-bottom: 5px;
`;

function App() {
    const [ rotation, setRotation ] = useState(flights.slice(2,1));
    const [ availableFlights, setAvailableFlights ] = useState(flights);

    // const onDragStart = useCallback((e, id) => {
    //     e.dataTransfer.setData("id", id);
    // }, []);

    // const onDrop = useCallback((e) => {
    //     let id = e.dataTransfer.getData("id");
    //     const flight = flights.filter((f) => f.ident === id);

    //     setRotation((prev) => ([ ...prev, flight ]));

    //     // Conditions to add
    //     // 1 - Airport of arrival from the previous flight should be the airport of departure of the next flight
    //     // 2 - The time difference between the time of arrival of the previous flight and the departure of the next 
    //     //  has to be 15 minutes or higher
    //     setAvailableFlights(prev => prev.filter((p) =>  p.ident !== id));
    // }, []);

    // const onDragOver = (e) => {
    //     e.preventDefault();
    // };

    // a little function to help us with reordering the result
    const reorder = (list, startIndex, endIndex) => {
        const result = Array.from(list);
        const [removed] = result.splice(startIndex, 1);
        result.splice(endIndex, 0, removed);

        return result;
    };

    /**
     * Moves an item from one list to another list.
     */
    const move = (source, destination, droppableSource, droppableDestination) => {
        const sourceClone = Array.from(source);
        const destClone = Array.from(destination);
        const [removed] = sourceClone.splice(droppableSource.index, 1);

        destClone.splice(droppableDestination.index, 0, removed);

        const result = {};
        result[droppableSource.droppableId] = sourceClone;
        result[droppableDestination.droppableId] = destClone;

        return result;
    };



    const onDragEnd = (result) => {
        const { source, destination, draggableId } = result;

        // dropped outside the list
        if (!destination) {
            return;
        }

        console.log(result);

        // if (source.droppableId === destination.droppableId) {
        //     const items = reorder(
        //         getList(source.droppableId),
        //         source.index,
        //         destination.index
        //     );
            const flight = flights.find(f => f.ident === draggableId);

            if (source.droppableId === "droppable") {
                state = { selected: items };
                setRotation(prev => [...prev, flight]);

            }

        //     this.setState(state);
        // } else {
        //     const result = move(
        //         getList(source.droppableId),
        //         getList(destination.droppableId),
        //         source,
        //         destination
        //     );

        //     this.setState({
        //         items: result.droppable,
        //         selected: result.droppable2
        //     });
        // }
    };


    return (
        <DragDropContext onDragEnd={onDragEnd}>
            <div className="App">
                <div>Header</div>
                <Wrapper>

                    {/* Aircrafts List */}
                    <Header>Aircrafts</Header>
                    <List>
                        {aircrafts.map((aircraft) => (
                            <AircraftCard
                                key={aircraft.ident}
                                aircraft={aircraft}
                            />
                        ))}
                    </List>

                    <Droppable droppableId={"droppable"}>
                        {(provided, snapshot) => (
                            <>
                            {/* Rotation */}
                            <Header>Rotation</Header>
                            <List
                                innerRef={provided.innerRef}
                                >
                                {rotation.map((flight) => {
                                    return (
                                    <RotationCard
                                        key={flight.ident}
                                        flight={flight}
                                    />
                                );
                                })}
                            </List>


                            {/* Flight List */}
                            <div>
                                {availableFlights.map((flight, index) => { 
                                    return (<Flight key={flight.ident} index={index} flight={flight}/>);
                                    })
                                }
                                {provided.placeholder}
                            </div>

                            </>
                            )}
                    </Droppable>
                </Wrapper>

            </div>
        </DragDropContext>
    );
}

export default App;
