import React, { useState } from "react";
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import styled from "styled-components";
import { AircraftCard } from "./components/AircraftCard";
import Flight from "./components/Flight/Flight";
import { List } from "./components/List";
import { move } from "./utils/helpers";
import Timeline from "./components/Timeline/Timeline";
import { aircrafts, flights } from "./utils/data";

const Wrapper = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const Header = styled.h2`
    padding-bottom: 5px;
`;

function App() {
    const [ rotation, setRotation ] = useState(flights.slice(2,1));
    const [ availableFlights, setAvailableFlights ] = useState(flights);

    const onDragEnd = (result) => {
        const { source, destination, draggableId } = result;

        console.log(result);

        // Dropped outside the list
        if (!destination) {
            return;
        }

        if (source.droppableId == "rotation") {
            return;
        }

        if (destination.droppableId === "droppable") return;

        if (destination.droppableId == "rotation") {

            // Departure Airport of the flight must be the same of the previous flight
            if(checkIfValid(draggableId, destination.index)) {
                const r = move(availableFlights, rotation, source, destination);
                setRotation(r.rotation);
                setAvailableFlights(r.droppable);
            }

            return;
        }
    };

    // previousFlight id -> destionation -> index ----> rotation - index - 1
    const checkIfValid = (flightId, previousFlightId) => {
        const flight = flights.find(f => f.ident === flightId);
        const previousFlight = rotation[previousFlightId - 1] || null;
        const nextFlight = rotation[previousFlightId + 1] || null;

        // First flight in rotation
        if(previousFlight && nextFlight) {
            return true;
        }

        // 1200 seconds = 20 minutes
        if( previousFlight &&
            previousFlight.departuretime + 1200 <= flight.arrivaltime 
            && previousFlight.destination === flight.origin) {
            return true;
        }

        if( nextFlight
            && flight.departuretime + 1200 <= nextFlight.arrivaltime
            && flight.destination === nextFlight.origin ) {
            return true;
        }

        return;
    }

    return (
        <DragDropContext onDragEnd={onDragEnd}>
            <div className="App">
                <div>Header</div>
                <Wrapper>
                    {/* Aircrafts List */}
                    <div style={{ display: "flex", flexDirection: "column", alignItems: "center"}}>
                        <Header>Aircrafts</Header>
                        <List>
                            {aircrafts.map((aircraft) => (
                                <AircraftCard key={aircraft.ident} aircraft={aircraft} />
                            ))}
                        </List>
                    </div>
                    {/* Rotation */}
                    <div style={{ display: "flex", flexDirection: "column", alignItems: "center",  width: "250px" }}>
                        <Header>Rotation</Header>

                        <Droppable droppableId={"rotation"}>
                            {(provided) => (
                                <div ref={provided.innerRef} style={{  width: "200px"}}>
                                    {rotation.map((flight, index) => (
                                        <Flight
                                        isDragDisabled={true}
                                        key={flight.ident}
                                        index={index}
                                        flight={flight}
                                    />))}
                                    {provided.placeholder}
                                </div>
                            )}
                        </Droppable>
                        test
                        <Timeline />
                     </div>
                    {/* Flight List */}
                    <div style={{ display: "flex", flexDirection: "column", alignItems: "center",  width: "250px" }}>
                    <Header>Available Flights</Header>
                    <Droppable droppableId={"droppable"}>
                        {(provided) => (
                            <div  ref={provided.innerRef}>
                                {availableFlights.map((flight, index) => (
                                    <Flight
                                        key={flight.ident}
                                        index={index}
                                        flight={flight}
                                    />)
                                )}
                                {provided.placeholder}
                            </div>)}
                    </Droppable>
                    </div>
                </Wrapper>
            </div>
        </DragDropContext>);
}

export default App;
