import React, { useCallback, useState } from "react";
import styled from "styled-components";
import { AircraftCard } from "./components/AircraftCard";
import { FlightCard } from "./components/FlightCard";
import { aircrafts, flights } from "./utils/data";

const Wrapper = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const List = styled.div`
  text-align: center;
  padding: 10px;
  width: 200px;
  height: calc(100vh - 60px);
  overflow-y: scroll;
  overflow-x: hidden;
  border: 2px solid red;
  margin: 0 5px 0 5px;
  box-shadow: inset 0 8px 10px -6px black;
`;

const Header = styled.h2`
    padding-bottom: 5px;
`;

function App() {
    const [ rotation, setRotation ] = useState([]);
    const [ availableFlights, setAvailableFlights ] = useState(flights);

    const onDragStart = useCallback((e, id) => {
        e.dataTransfer.setData("id", id);
    }, []);

    const onDrop = useCallback((e) => {
        let id = e.dataTransfer.getData("id");
        const flight = flights.filter((f) => f.ident === id);

        setRotation((prev) => ([ ...prev, flight ]));

        setAvailableFlights(prev => prev.filter((p) => p.indent !== id));
    }, []);

    const onDragOver = (e) => {
        e.preventDefault();
    };


    return (
        <div className="App">
            <div>Header</div>
            <Wrapper>
                <Header>Aircrafts</Header>
                <List>
                    {aircrafts.map((aircraft) => (
                        <AircraftCard
                            key={aircraft.ident}
                            aircraft={aircraft}
                        />
                    ))}
                </List>
                <Header>Rotation</Header>
                <List
                    onDragOver={onDragOver}
                    onDrop={onDrop}>
                    {rotation.map((flight) => (
                        <FlightCard
                            key={flight.ident}
                            flight={flight}
                        />
                    ))}
                </List>
                <Header>Flights</Header>
                <List>
                    {availableFlights.map((flight) => (
                        <FlightCard
                            key={flight.ident}
                            flight={flight}
                            onDragStart={(e) => onDragStart(e, flight.ident)}
                        />
                    ))}
                </List>
            </Wrapper>
        </div>
    );
}

export default App;
