/* eslint-disable react/jsx-indent */
/* eslint-disable indent */
import React, { useState } from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import styled from "styled-components";
import { AircraftCard } from "./components/AircraftCard";
import Flight from "./components/Flight/Flight";
import { FlightCard } from "./components/FlightCard";
import { List } from "./components/List";
import { RotationCard } from "./components/RotationCard";
import { aircrafts, flights } from "./utils/data";


const Wrapper = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;

const Header = styled.h2`
    padding-bottom: 5px;
`;

function App() {
    const [ rotation, setRotation ] = useState([]);
    const [ availableFlights, setAvailableFlights ] = useState(flights);

    // const onDragStart = useCallback((e, id) => {
    //     e.dataTransfer.setData("id", id);
    // }, []);

    // const onDrop = useCallback((e) => {
    //     let id = e.dataTransfer.getData("id");
    //     const flight = flights.filter((f) => f.ident === id);

    //     setRotation((prev) => ([ ...prev, flight ]));

    //     // Conditions to add
    //     // 1 - Airport of arrival from the previous flight should be the airport of departure of the next flight
    //     // 2 - The time difference between the time of arrival of the previous flight and the departure of the next 
    //     //  has to be 15 minutes or higher
    //     setAvailableFlights(prev => prev.filter((p) =>  p.ident !== id));
    // }, []);

    // const onDragOver = (e) => {
    //     e.preventDefault();
    // };

    return (
        <DragDropContext onDragEnd={result => console.log(result)}>
            <div className="App">
                <div>Header</div>
                <Wrapper>
                    <Header>Aircrafts</Header>
                    <List>
                        {aircrafts.map((aircraft) => (
                            <AircraftCard
                                key={aircraft.ident}
                                aircraft={aircraft}
                            />
                        ))}
                    </List>
                    <Droppable droppableId={"droppable"}>
                        {(provided, snapshot) => (
                            <>
                            {/* <Header>Rotation</Header> */}
                            <List
                                innerRef={provided.innerRef}
                                >
                                {rotation.map((flight) => (
                                    <RotationCard
                                        key={flight.ident}
                                        flight={flight}
                                    />
                                ))}
                            </List>

                            {/* <Header>Flights</Header> */}
                            <List>
                                {availableFlights.map((flight, index) => { 
                                    return (<Flight key={flight.ident} index={index} flight={flight}/>);
                                    })
                                }
                            </List>

                            </>
                            )}
                    </Droppable>
                </Wrapper>

            </div>
        </DragDropContext>
    );
}

export default App;
