import React, {useEffect, useState} from "react";
import styled from "styled-components";

const Busy = styled.div`
    height: 15px;
    width: ${({ duration }) => `${duration}%` };
    background-color: red;
`

const Turnaround = styled.div`
    height: 15px;
    width: ${({ duration }) => `${duration}%` };
    background-color: purple;
`

const Free = styled.div`
    height: 15px;
    width: ${({ duration }) => `${duration}%` };
    background-color: green;
`
const Bar = styled.div`
    display: flex;
    flex-direction: row;
    width: 200px;

    &:first-child {
        border-radius: 10px 10px 10px 10px;
    }
`

// 1440 min -> 24 hours
const Timeline = (periods = []) => {
    const [ timeline, setTimeline ] = useState([]);

    useEffect(() => {
        setTimeline(periods.periods);
    }, [periods])

    console.log(timeline);

    return (
    <Bar>
        {timeline.length !== 0 ? 
            timeline.map((segment, index) => {
                if (segment.type === "Busy") return (<Busy key={index} duration={segment.duration / 864}/>);
                if (segment.type === "Turnaround") return (<Turnaround  key={index}  duration={segment.duration / 864}/>);
                if (segment.type === "Free") return (<Free  key={index}  duration={segment.duration / 864}/>);
            }) : (<Free duration={100}></Free>)
        }
    </Bar>
    )
}

export default Timeline;
