import React from "react";
import { Card } from "../Card";
import styled from "styled-components";

const Row = styled.li`
   display: flex;
   flex-direction: row;
   align-items: center;
`;

const Column = styled.div`
   display: flex;
   flex-direction: column;
`;

type Flight = {
    ident: string,
    departuretime: number,
    arrivaltime: number,
    readable_departure: string,
    readable_arrival: string,
    origin: string,
    destination: string,
} 


type Props = {
    flight: Flight,
    onDragStart: any,
} 


const FlightCard = ({ flight, onDragStart} : Props) => {
    const { ident, readable_departure, readable_arrival, origin, destination } = flight;

    return (
        <Card onDragStart={onDragStart} draggable={true}>
            <div style={{ flex: "1", position: "relative"}}>
                <div>
                    <p>{ident}</p>
                </div>
                <div>
                    <Column>
                        <p>{readable_departure}</p>
                        <p>{origin}</p>
                    </Column>
                    <Column>
                        <p>{readable_arrival}</p>
                        <p>{destination}</p>
                    </Column>
                </div>
            </div>
        </Card>);
};

export default FlightCard;
