import React, { useMemo } from "react";
import { Droppable } from "react-beautiful-dnd";
import Timeline from "../Timeline/Timeline";
import { calculateTimePeriods } from "../../utils/helpers/timeline";
import styled from "styled-components";

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 250px 
`;

const Header = styled.h2`
    padding-bottom: 5px;
`;

const RotationList = ({ rotation }) => {
    const periods = useMemo(() => calculateTimePeriods(rotation), [rotation]);


    return (
    <Wrapper>
        <Header>Rotation</Header>
        <Droppable droppableId={"rotation"}>
            {(provided) => (
                <List innerRef={provided.innerRef} style={{  width: "200px"}}>
                    {rotation.map((flight, index) => (
                        <Flight
                        isDragDisabled={true}
                        key={flight.ident}
                        index={index}
                        flight={flight}
                    />))}
                    {provided.placeholder}
                </List>
            )}
        </Droppable>
        <Timeline periods={periods} />
    </Wrapper>);
}

export default RotationList;
