import React, { useRef } from "react";
import styled from "styled-components";

const ListComponent = styled.div`
    text-align: center;
    padding: 10px;
    width: 200px;
    height: calc(100vh - 60px);
    margin: 0 5px 0 5px;
    clip-path: inset(0 -30px 0 0);
`;

// box-shadow:  ${({ ref }) => ref?.current.offsetHeight > 0 ? "inset 0px 11px 8px -5px #CCC, inset 0px -11px 8px -5px #CCC;" : ""};


const List = ({ innerRef, children}) => {
    return (
        <div
            ref={innerRef}
            style={{
                textAlign: "center",
                padding: "10px",
                width: "200px",
                clipPath: "inset(0 -30px 0 0)"}}>
            {children}
        </div>);
};

export default List;
